<script setup>
import { useArtist } from "@/composables/artist";

const props = defineProps({
  data: {
    type: Object,
    required: true,
  },
  artistFollowedProp: {
    type: Boolean,
    required: true,
  },
});

const { userFollowsArtist, updateArtistFollowing } = useArtist();
const filledStars = ref(0);
const emptyStars = ref(0);
const artistFollowed = ref();
const formatFollower = (num, precision = 2) => {
  const map = [
    { suffix: "T", threshold: 1e12 },
    { suffix: "B", threshold: 1e9 },
    { suffix: "M", threshold: 1e6 },
    { suffix: "K", threshold: 1e3 },
    { suffix: "", threshold: 1 },
  ];

  const found = map.find((x) => Math.abs(num) >= x.threshold);
  if (found) {
    const formatted = (num / found.threshold).toFixed(precision) + found.suffix;
    return formatted;
  }

  return num;
};

const calculatePercentage = () => {
  // Assuming the maximum rating is 5 and the maximum percentage is 100.
  const maxRating = 5;
  const maxPercentage = 100;

  // Calculate the percentage.
  filledStars.value = Math.floor(
    (props.data.popularity / maxPercentage) * maxRating
  );

  emptyStars.value = Math.floor(maxRating - filledStars.value);
};

const updateFollowStatus = (id) => {
  updateArtistFollowing(id, artistFollowed.value ? "unfollow" : "follow");
  artistFollowed.value = !artistFollowed.value;
};

watchEffect(() => (artistFollowed.value = props.artistFollowedProp));

onMounted(() => {
  calculatePercentage();
});
</script>
